import React, { useState, Fragment } from 'react';

function Cart() {
  const [cartItems, setCartItems] = useState([
    // Example data structure for cartItems
    {
      id: 1,
      products: {
        images: [
          { image: './images/products/1.jpg' }
        ]
      }
    },
    // Add more items as needed
  ]);

  return (
    <div>
      {cartItems.map((item) => (
        <Fragment key={item.id}>
          <hr />
          <div className="cart-item">
            <div className="row">
              <div className="col-4 col-lg-3">
                <img src={item.products.images[0].image} alt="Product" height="90" width="115" />
              </div>

              <div className="col-5 col-lg-3">
                <a href="#">OPPO F21s Pro 5G (Dawnlight Gold, 8GB RAM, 128 Storage) with No Cost EMI/Additional Exchange Offers</a>
              </div>

              <div className="col-4 col-lg-2 mt-4 mt-lg-0">
                <p id="card_item_price">$245.67</p>
              </div>

              <div className="col-4 col-lg-3 mt-4 mt-lg-0">
                <div className="stockCounter d-inline">
                  <span className="btn btn-danger minus">-</span>
                  <input type="number" className="form-control count d-inline" value="1" readOnly />
                  <span className="btn btn-primary plus">+</span>
                </div>
              </div>

              <div className="col-4 col-lg-1 mt-4 mt-lg-0">
                <i id="delete_cart_item" className="fa fa-trash btn btn-danger"></i>
              </div>
            </div>
          </div>
        </Fragment>
      ))}
    </div>
  );
}

export default Cart;
